    // @codingStandardsIgnoreEnd
    /**
     * Create a CMS page
     * 
     * @param array $cmsPageData Create a CMS page
     */
    public function createCmsPage($cmsPageData)
    {
        foreach ($cmsPageData as $cmsPage) {
           
            $activeData = [
                'is_active' => 1, // define active status
                'stores' => [0], // assign to stores
               ];
            $cmsPage = array_merge($cmsPage, $activeData);
            $existPageCheck = $this->pageFactory->create()->load($cmsPage['identifier'], 'identifier');
            if (empty($existPageCheck->getContent())) {
                $this->pageFactory->create()->setData($cmsPage)->save();
            } else {
                $existPageCheck->setContent($cmsPage['content']);
                $existPageCheck->save();
            }
        }
    }

   

