namespace ShipperHQ\Logger\Block\Adminhtml\Logger;

class View extends \Magento\Backend\Block\Widget\Form\Generic//\Magento\Backend\Block\Widget\Form\Container
{
    protected $_template = 'view.phtml';

    /**
     * @param \Magento\Backend\Block\Widget\Context $context
     * @param \Magento\Framework\Registry $registry
     * @param array $data
     */
    public function __construct(
        \Magento\Backend\Block\Widget\Context $context,
        \Magento\Framework\Registry $registry,
        \Magento\Framework\Data\FormFactory $formFactory,
        array $data = []
    ) {
        $this->_coreRegistry = $registry;
        parent::__construct($context, $registry, $formFactory, $data);
    }

    /**
     * Initialize form
     * Add standard buttons
     * Add "Save and Apply" button
     * Add "Save and Continue" button
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_controller = 'shqlogger';
        $this->_blockGroup = 'ShipperHQ_Logger';
        $this->_headerText = __('ShipperHQ Log Record');
        parent::_construct();
    }

    /**
     * Getter for form header text
     *
     * @return \Magento\Framework\Phrase
     */
    public function getHeaderText()
    {
        return __('ShipperHQ Log');
    }

    public function getMessageData()
    {
        $log = $this->getData('log');
        return $log;
    }

    public function getBackUrl()
    {
        return $this->getUrl('shqlogger/*/index');
    }
}
